class Solution:
    def maximumUnitsForce(self, boxTypes, truckSize):
        #贪心，每次取最大的就完事了
        flat = {}
        for row in boxTypes:
            if flat.get(row[1]) == None:
                flat[row[1]] = row[0]
            else:
                flat[row[1]] = flat[row[1]] + row[0]
        ans = 0
        for i in range(truckSize):
            if len(flat) == 0:
                return ans
            maxUnit = max(flat)
            ans += maxUnit
            if flat[maxUnit] == 1:
                flat.pop(maxUnit)
            else:
                flat[maxUnit] = flat[maxUnit] - 1
        return ans

    def max2(self,boxTypes,truckSize):
        boxTypes.sort(key=lambda x:x[1],reverse=True)
        ans = 0
        for row in boxTypes:
            if truckSize >= row[0]:
                ans += (row[0]*row[1])
                truckSize -= row[0]
            else:
                ans += (truckSize*row[1])
                truckSize = 0
            if truckSize == 0:
                break
        return ans


